              padding: const EdgeInsets.all(16),
              decoration: BoxDecoration(
                color: Colors.green[100],
                borderRadius: BorderRadius.circular(10),
              ),
              child: Row(
                children: [
                  // Add an image for the summary section
                  Image.asset(
                    'assets/images/sales_summary.png', // Placeholder image for summary
                    width: 60,
                    height: 60,
                  ),
                  const SizedBox(width: 16),
                  // Display Total Sales and Items Sold
                  Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      const Text(
                        'Sales Summary',
                        style: TextStyle(
                          fontSize: 18,
                          fontWeight: FontWeight.bold,
                        ),
                      ),
                      const SizedBox(height: 10),
                      Text('Total Sales: â‚¹${totalSales.toStringAsFixed(2)}'),
                      Text('Total Items Sold: $totalItemsSold'),
                    ],
                  ),
                ],
              ),
            ),
            const SizedBox(height: 20),

            // Sales List Section
            Expanded(
              child: ListView.builder(
                itemCount: salesData.length,
                itemBuilder: (context, index) {
                  final sale = salesData[index];
                  return Card(
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(10),
                    ),
                    elevation: 4,
                    margin: const EdgeInsets.only(bottom: 16),
                    child: ListTile(
                      contentPadding: const EdgeInsets.all(16.0),
                      leading: Image.asset(
                        sale['image'], // Use the image path from the sales data
                        width: 50,
                        height: 50,
                      ),
                      title: Text(
                        'Date: ${sale['date']}',
                        style: const TextStyle(fontWeight: FontWeight.bold),
                      ),
                      subtitle: Column(
                        crossAxisAlignment: CrossAxisAlignment.start,
                        children: [
                          Text('Product: ${sale['product']}'),
                          Text('Quantity: ${sale['quantity']}'),
                          Text(
                            'Total Amount: â‚¹${sale['totalAmount'].toStringAsFixed(2)}',
                          ),
                        ],
                      ),
                    ),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}




