class OrderManagementPage extends StatelessWidget {
  final List<Map<String, dynamic>> orders = [
    {
      'orderId': 'ORD001',
      'customerName': 'John Doe',
      'orderDate': '2024-11-15',
      'status': 'Pending',
      'totalAmount': 150.0,
    },
    {
      'orderId': 'ORD002',
      'customerName': 'Jane Smith',
      'orderDate': '2024-11-17',
      'status': 'Shipped',
      'totalAmount': 200.0,
    },
    {
      'orderId': 'ORD003',
      'customerName': 'Alex Brown',
      'orderDate': '2024-11-18',
      'status': 'Delivered',
      'totalAmount': 100.0,
    },
  ];

  const OrderManagementPage({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: const Color(0xFF3EA120),
        title: const Text('Order Management'),
        centerTitle: true,
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: ListView.builder(
          itemCount: orders.length,
          itemBuilder: (context, index) {
            final order = orders[index];
            return _buildOrderCard(
              context,
              orderId: order['orderId'],
              customerName: order['customerName'],
              orderDate: order['orderDate'],
              status: order['status'],
              totalAmount: order['totalAmount'],
            );
          },
        ),
      ),
    );
  }

  Widget _buildOrderCard(
    BuildContext context, {
    required String orderId,
    required String customerName,
    required String orderDate,
    required String status,
    required double totalAmount,
  }) {
    return Card(
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(10)),
      elevation: 4,
      margin: const EdgeInsets.only(bottom: 16),
      child: ListTile(
        contentPadding: const EdgeInsets.all(16.0),
        title: Text(
          'Order ID: $orderId',
          style: const TextStyle(fontWeight: FontWeight.bold),
        ),
        subtitle: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Customer: $customerName'),
            Text('Order Date: $orderDate'),
            Text('Total Amount: â‚¹${totalAmount.toStringAsFixed(2)}'),
            const SizedBox(height: 8),
            Row(
              children: [
                Text(
                  'Status: $status',
                  style: TextStyle(
                    fontWeight: FontWeight.bold,
                    color: _getStatusColor(status),
                  ),
                ),
                const SizedBox(width: 8),
                ElevatedButton(
                  style: ElevatedButton.styleFrom(
                    backgroundColor: const Color(0xFF3EA120),
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(10),
                    ),
                  ),
                  onPressed: () {
                    _updateOrderStatus(context, orderId, status);
                  },
                  child: const Text(
                    'Update Status',
                    style: TextStyle(color: Colors.white),
                  ),
                ),
              ],
            ),
          ],
        ),
      ),
    );
  }

  // Function to change the status color based on the order status
  Color _getStatusColor(String status) {
    switch (status) {
      case 'Pending':
        return Colors.orange;
      case 'Shipped':
        return Colors.blue;
      case 'Delivered':
        return Colors.green;
      default:
        return Colors.black;
    }
  }

  // Function to simulate order status update
  void _updateOrderStatus(
      BuildContext context, String orderId, String currentStatus) {
    String newStatus;
    if (currentStatus == 'Pending') {
      newStatus = 'Shipped';
    } else if (currentStatus == 'Shipped') {
      newStatus = 'Delivered';
    } else {
      newStatus = 'Pending'; // Reset to Pending for demo
    }

    // Show a confirmation message
    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(content: Text('Order $orderId status updated to $newStatus')),
    );
  }
}

